// About.js

import React from "react";
import "./About.css"; // Import the CSS file
const About = () => {
  return (
    <div>
      <h2>About Us</h2>
      <p>
        Welcome to our Ecommerce website. At Ecommerce Website, our mission is to
        provide high-quality products with an exceptional online shopping
        experience. We believe in delivering top-notch service and building
        lasting relationships with our customers.
      </p>
      <p>
        Our diverse range of products caters to various interests and needs.
        Whether you're looking for the latest fashion trends, tech gadgets,
        home essentials, or unique gifts, we've got you covered.
      </p>
      <p>
        Explore our online store, discover new arrivals, and enjoy the
        convenience of shopping from the comfort of your home. Thank you for
        choosing Ecommerce Website for your online shopping needs.
      </p>
    </div>
  );
};

export default About;
