import React from "react";
import { Link, useLocation } from "react-router-dom";
import { ShoppingCart } from "phosphor-react";
import "./navbar.css";

export const Navbar = () => {
  const location = useLocation();

  return (
    <div className="navbar">
      <div className="logo">
        <Link to="/" id="NavTitle">
          Ecommerce website
        </Link>
      </div>
      <div className="links">
        <Link to="/" className={location.pathname === "/" ? "active-link" : ""}>
          Shop
        </Link>
        <Link to="/About" className={location.pathname === "/About" ? "active-link" : ""}>
          About
        </Link>
        <Link
          to="/login"
          className={location.pathname === "/login" ? "active-link" : ""}
        >
          Login
        </Link>
        <Link
          to="/contact"
          className={location.pathname === "/contact" ? "active-link" : ""}
        >
          Contact
        </Link>
        <Link
          to="/cart"
          id="cartlink"
          className={location.pathname === "/cart" ? "active-link" : ""}
        >
          <ShoppingCart size={32} />
          {/* cartItemCount can be displayed here if needed */}
        </Link>
        <Link
          to="/logout"
          className={location.pathname === "/logout" ? "active-link" : ""}
        >
          Logout
        </Link>
      </div>
    </div>
  );
};
