// AddUser.js
import React, { useState } from 'react';
import Card from './UI/Card';
import './Users/AddUser.css';

const AddUser = (props) => {
  const [enteredUsername, setEnteredUsername] = useState('');
  const [enteredAge, setEnteredAge] = useState('');
  const [error, setError] = useState(null);

  const addUserHandler = (event) => {
    event.preventDefault();

    if (enteredUsername.trim().length === 0 || enteredAge.trim().length === 0) {
      setError('Please enter a valid username and age.');
      return;
    }

    if (+enteredAge < 1) {
      setError('Please enter a valid age (> 0).');
      return;
    }

    // Reset error state if there are no validation errors
    setError(null);

    // Process the submitted data (you can pass it to a parent component or perform other actions)
    console.log('Submitted:', { username: enteredUsername, age: enteredAge });

    // Reset input fields after submission
    setEnteredUsername('');
    setEnteredAge('');
  };

  return (
    <Card>
      <form onSubmit={addUserHandler}>
        <label htmlFor="username">Username</label>
        <input
          id="username"
          type="text"
          value={enteredUsername}
          onChange={(e) => setEnteredUsername(e.target.value)}
        />

        <label htmlFor="age">Age (Years)</label>
        <input
          id="age"
          type="number"
          value={enteredAge}
          onChange={(e) => setEnteredAge(e.target.value)}
        />

        <button type="submit">Add User</button>
      </form>

      {error && <p className="error">{error}</p>}
    </Card>
  );
};

export default AddUser;
