import React, { useState } from 'react';
import Button from '../../UI/Button/Button';
import './CourseInput.css';

const CourseInput = (props) => {
  const [enteredValue, setEnteredValue] = useState('');
  const [isValid, setIsValid] = useState(true);
  const [isSingleLetter, setIsSingleLetter] = useState(false);

  const goalInputChangeHandler = (event) => {
    const inputValue = event.target.value.trim();

    if (inputValue.length === 1) {
      setIsSingleLetter(true);
    } else {
      setIsSingleLetter(false);
    }

    if (inputValue.length > 0) {
      setIsValid(true);
    } else {
      setIsValid(false);
    }

    setEnteredValue(inputValue);
  };

  const formSubmitHandler = (event) => {
    event.preventDefault();

    if (enteredValue.length === 0) {
      setIsValid(false);
      return;
    }

    props.onAddGoal(enteredValue);
    setEnteredValue('');
  };

  return (
    <form onSubmit={formSubmitHandler}>

      {/* Using template literals for dynamic class names */}
      <div className={`form-control ${!isValid ? 'invalid' : ''}`}>
        <label>Course Goal</label>
        <input type="text" onChange={goalInputChangeHandler} value={enteredValue} />
      </div>

      <Button type="submit" style={{ backgroundColor: isSingleLetter ? 'red' : '' }}>
        Add Goal
      </Button>
    </form>
  );
};

export default CourseInput;
