import React from "react";
import { Link, useLocation } from "react-router-dom";
import { ShoppingCart } from "phosphor-react";
import "./navbar.css";
// Assuming you have a ShopContext or CartContext to manage cart state

// Import necessary dependencies and components

export const Navbar = () => {
  const location = useLocation();
  const { cartItems } = useContext(ShopContext); // Replace ShopContext with your actual context

  return (
    <div className="navbar">
      <div className="logo">
        <Link to="/" id="NavTitle">
          Ecommerce website
        </Link>
      </div>
      <div className="links">
        <Link to="/" className={location.pathname === "/" ? "active-link" : ""}>
          Shop
        </Link>
        <Link to="/about" className={location.pathname === "/about" ? "active-link" : ""}>
          About
        </Link>
        <Link to="/contact" className={location.pathname === "/contact" ? "active-link" : ""}>
          Contact
        </Link>
        <Link to="/cart" id="cartlink" className={location.pathname === "/cart" ? "active-link" : ""}>
          <ShoppingCart size={32} />
          {cartItems.length > 0 && <span className="cart-count">{cartItems.length}</span>}
        </Link>
      </div>
    </div>
  );
};
