import React from "react";
import { Link, useLocation } from "react-router-dom";
import { ShoppingCart } from "phosphor-react";
import "./navbar.css";
import { ShopContext } from "../../context/shop-context"; // Import the ShopContext

export const Navbar = () => {
  const location = useLocation();
  const { cartItems } = useContext(ShopContext); // Access cartItems from the context

  // Calculate the total number of items in the cart
  const cartItemCount = Object.values(cartItems).reduce(
    (accumulator, currentValue) => accumulator + currentValue,
    0
  );

  return (
    <div className="navbar">
      <div className="logo">
        <Link to="/" id="NavTitle">
          Ecommerce website
        </Link>
      </div>
      <div className="links">
        <Link to="/" className={location.pathname === "/" ? "active-link" : ""}>
          Shop
        </Link>
        <Link to="/About" className={location.pathname === "/About" ? "active-link" : ""}>
          About
        </Link>
        <Link
          to="/contact"
          className={location.pathname === "/contact" ? "active-link" : ""}
        >
          Contact
        </Link>
        <Link
          to="/cart"
          id="cartlink"
          className={location.pathname === "/cart" ? "active-link" : ""}
        >
          <ShoppingCart size={32} />
          {cartItemCount > 0 && (
            <span className="badge">{cartItemCount}</span>
          )}
        </Link>
      </div>
    </div>
  );
};
